import React, { useState, useEffect } from 'react';
import './CommonVerbs.css'

import emoji from 'react-easy-emoji'

const CommonVerbs = ({ setVerb }) => {

  const [hasError, setErrors] = useState(false);
  const [loading, setLoading] = useState(true);
  const [common_data, setCommonData] = useState({});

  async function fetchData(){
    const resVerbs = await fetch('http://localhost:5555/common-verbs', {
      method: "POST",
    });
    resVerbs
    .json()
    .then(res => {
        console.log('res')
        setCommonData(res)
        setLoading(false)
    })
    .catch(err => setErrors(err));
  }

  useEffect(() => {
    fetchData()
  }, []);


  if(loading === true){
    return <h1>Loading ...</h1>        
  } else {
    return (
        <div className="verbs-container">
            <h3>Common German Verbs { emoji('ðŸ˜»ðŸ¢›ðŸ¢ƒ') } </h3>
        { 
          JSON.parse(common_data.html).map((el, index) => (
            <p key={index} onClick={() => setVerb(el)}>{el}</p>
          ))
        }
        </div>
      );
  }
}

export default CommonVerbs;
