import React, { useState, useEffect } from 'react';
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "shards-react";



const options = [
  { key: 1, text: 'Präsens', value: 'Präsens' },
  { key: 2, text: 'Präteritum', value: 'Präteritum' },
  { key: 3, text: 'Futur I', value: 'Futur I' },
]

const DropdownClearable = ({ setTense, passed_tense }) => {
  // const [verb_tense, setVerbTense] = useState('Set Tense');
  const [open, setOpen] = useState(false);

  useEffect(() => {
  }, [passed_tense]);

  return(
    <Dropdown open={open} toggle={() => setOpen(true)}>
      <DropdownToggle theme="success">{passed_tense}</DropdownToggle>
      <DropdownMenu>
        <DropdownItem onClick={() => setTense('Präsens')} >Präsens</DropdownItem>
        <DropdownItem onClick={() => setTense('Präteritum')} >Präteritum</DropdownItem>
        <DropdownItem onClick={() => setTense('Futur I')} >Futur I</DropdownItem>
      </DropdownMenu>
    </Dropdown>
  )
}

export default DropdownClearable
