import React, { useState, useEffect } from 'react';
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "shards-react";



const options = [
  { key: 1, text: 'Präsens', value: 'Präsens' },
  { key: 2, text: 'Präteritum', value: 'Präteritum' },
  { key: 3, text: 'Futur', value: 'Futur I' },
]

const DropdownClearable = () => {
  const [verb_tense, setVerbTense] = useState('Set Tense');
  const [open, setOpen] = useState(false);

  return(
    <Dropdown open={open} toggle={() => setOpen(!open)}>
      <DropdownToggle theme="success">Dropdown</DropdownToggle>
      <DropdownMenu>
        <DropdownItem onClick={() => setVerbTense('Präsens')} >Präsens</DropdownItem>
        <DropdownItem onClick={() => setVerbTense('Präteritum')} >Präteritum</DropdownItem>
        <DropdownItem onClick={() => setVerbTense('Futur I')} >Futur I</DropdownItem>
      </DropdownMenu>
    </Dropdown>
  )
}

export default DropdownClearable
