import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

const Verb = ({ passed_verb, passed_tense }) => {
  const [hasError, setErrors] = useState(false);
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState({});

  let something = {
    tense: passed_tense,
    verb: passed_verb
  }
  async function fetchData(){
    const res = await fetch('http://localhost:5555/conjugate', {
      method: "POST",
      body: JSON.stringify(something)
    });
    res
    .json()
    .then(res => {
        if(res.html.includes('Pr&#xE4;sens')== true){
          res.html.replace('Pr&#xE4;sens', '')
        } else if(res.html.includes('Pr&#xE4;sens') == true){
          res.html.replace('Pr&#xE4;teritum', '')
        } else if(res.html.includes('Pr&#xE4;sens')== true){
          res.html.replace('Futur I', '')
        }
        setData(res);
        setLoading(false)
    })
    .catch(err => setErrors(err));
  }

  useEffect(() => {
    console.log('component updated')
    fetchData()
  }, [passed_verb, passed_tense]);

  {console.log(JSON.stringify(data))}
  if(loading === true){
    return <h1>Loading ...</h1>        
  } else {
    let tenses = {
      text1: 'Pr채sens',
      text2: 'Pr채teritum',
      text3: 'Futur I',
    }
    return (
      <div>
        <div className="verbs-conjugated" dangerouslySetInnerHTML={{ __html: data.html }} />
      </div>
      );
  }
}

export default Verb;
