import React, { useState, useEffect } from 'react';
import { Input } from 'semantic-ui-react';
import { Button } from "shards-react";

import "bootstrap/dist/css/bootstrap.min.css";
import "shards-ui/dist/css/shards.min.css"

const InputVerb = ({ passed_verb, setVerb }) => {
    const [verb, setInputVerb] = useState({});
    useEffect(() => {
      }, [passed_verb]);

    return(
        <div>
            <Input loading onChange={(e) => setInputVerb(e.target.value)} placeholder='Ein Verb Hier' />
            <Button theme="secondary" onClick={() => setVerb(verb) }>Konjugierst!</Button> 
        </div>
    )
}


export default InputVerb
