import React, { useState, useEffect } from 'react';
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "shards-react";



const options = [
  { key: 1, text: 'Präsens', value: 'Präsens' },
  { key: 2, text: 'Präteritum', value: 'Präteritum' },
  { key: 3, text: 'Futur', value: 'Futur I' },
]

const DropdownClearable = () => {
  const [verb_tense, setVerbTense] = useState({});

  return(
    <Dropdown toggle={this.toggle}>
      <DropdownToggle theme="success">Dropdown</DropdownToggle>
      <DropdownMenu>
        <DropdownItem>Action</DropdownItem>
        <DropdownItem>Another action</DropdownItem>
        <DropdownItem>Something else here</DropdownItem>
      </DropdownMenu>
    </Dropdown>
  )
}

export default DropdownClearable
