import React, { useState, useEffect } from 'react';


const Examples = async ({ passed_verb }) => {

    const [examples, setExamples] = useState({});
    const [loading, setLoading] = useState(true); 
    const [hasError, setErrors] = useState(false);

    let something = {
        verb: passed_verb
    }

    async function fetchData(){
        // loading ? setLoading(false) : setLoading(true)

        const res = await fetch('http://localhost:5555/get-example', {
        method: "POST",
        body: JSON.stringify(something)
        });
        res
        .json()
        .then(res => {
            // console.log('examples')
            // console.log(res.sentences_arr)
            setLoading(false)
            setExamples(res);
        })
        .catch(err => setErrors(err));
    }

    useEffect(() => {
        fetchData()
        // console.log('sentences')
        // console.log(sentences.sentences_arr.splice(0, 2))
    }, [examples]);

    // if (sentences.length > 1){
    //     alert('yes it is')
    //     let chunk = sentences.sentences_arr.splice(0, 2)
    //     console.log('chunk')
    //     console.log(chunk)
    // }
    console.log(examples)
    if (loading == true){
        return <h1>Doing something</h1>
    } else {
        return(
            <div style={{width: '100%', marginTop: '15px'}}>
                {
                    {/* examples.sentences_arr.forEach((sentence) => {
                    return <p>{sentence}</p>
                }) */}
                }
            </div>
        )
    }
}


export default Examples
