import React, { useState, useEffect } from 'react';
import { Input, Button } from 'semantic-ui-react'

const InputVerb = ({ passed_verb, setVerb }) => {
    const [verb, setInputVerb] = useState({});
    useEffect(() => {
      }, [passed_verb]);

    return(
        <div>
            <Input loading onChange={(e) => setInputVerb(e.target.value)} placeholder='type verb here' />
            <Button onClick={() => setVerb(verb) }>Conjugate</Button> 
        </div>
    )
}


export default InputVerb
