import React, { useState, useEffect } from 'react';
import { Input } from 'semantic-ui-react';
import { Button, FormInput, InputGroup, InputGroupAddon, InputGroupText } from "shards-react";

const InputVerb = ({ passed_verb, setVerb }) => {
    const [verb, setInputVerb] = useState({});
    useEffect(() => {
      }, [passed_verb]);

    return(
        <div>
            <InputGroup>
                <InputGroupAddon type="prepend">
                <InputGroupText>Verb</InputGroupText>
                </InputGroupAddon>
                <FormInput placeholder='Ein Verb Hier' />
                <InputGroupAddon type="append">
                <Button theme="primary">Konjugierst!</Button>
                </InputGroupAddon>
            </InputGroup>

            <FormInput valid className="mb-2" onChange={(e) => setInputVerb(e.target.value)} placeholder='Ein Verb Hier' />
            <Button theme="primary" onClick={() => setVerb(verb) }>Konjugierst!</Button> 
        </div>
    )
}


export default InputVerb
