import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import Verb from './Verb'
import DropdownClearable from './DropdownClearable';
import Sidebar from './Sidebar'
import CommonVerbs from './CommonVerbs'
import InputVerb from './InputVerb'
import { Alert } from "shards-react";

import "bootstrap/dist/css/bootstrap.min.css";
import "shards-ui/dist/css/shards.min.css"


function App() {
  const [passed_verb, setVerb] = useState("tanzen");

  return (
    <div className="flex-container">
        <Alert theme="primary">
          Here is an alert
        </Alert>
        <Sidebar width={150} height={"100vh"}>
          <CommonVerbs setVerb={setVerb} />
        </Sidebar>
        <InputVerb passed_verb={passed_verb} setVerb={setVerb} />
        <div className="main-content">
          { console.log('passed_verb') }
          { console.log(passed_verb) }
          <Verb passed_verb={passed_verb} />
          <DropdownClearable />
        </div>
    </div>
  );
}

export default App;
