import React, { useState, useEffect } from 'react';
import { Input, Button } from 'semantic-ui-react'

const InputVerb = ({ passed_verb, setVerb }) => {
    const [verb, setInputVerb] = useState({});
    useEffect(() => {
        console.log('component updated')
        fetchData()
      }, [passed_verb]);

    return(
        <div>
            <Input loading onChange={(e) => setInputVerb(e.target.value)} placeholder='Search...' />
            <Button onClick={() => console.log(passed_verb) }>Click Here</Button> 
        </div>
    )
}


export default InputVerb
