import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';

const Verb = ({ passed_verb, passed_tense }) => {
  const [hasError, setErrors] = useState(false);
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState({});

  let something = {
    tense: passed_tense,
    verb: passed_verb
  }
  async function fetchData(){
    const res = await fetch('http://localhost:5555/conjugate', {
      method: "POST",
      body: JSON.stringify(something)
    });
    res
    .json()
    .then(res => {
        setData(res);
        setLoading(false)
    })
    .catch(err => setErrors(err));
  }

  useEffect(() => {
    console.log('component updated')
    fetchData()
  }, [passed_verb, passed_tense]);

  {console.log(JSON.stringify(data))}
  if(loading === true){
    return <h1>Loading ...</h1>        
  } else {
    console.log(typeof(data.html))
    let new_html = JSON.parse(data.html)
    new_html.removeChild('p')
    return (
      <div>
        <div className="verbs-conjugated" dangerouslySetInnerHTML={{ __html: new_html }} />
      </div>
      );
  }
}

export default Verb;
