import React, { useState, useEffect } from 'react';
import { Input } from 'semantic-ui-react';
import { Button, FormInput } from "shards-react";

const InputVerb = ({ passed_verb, setVerb }) => {
    const [verb, setInputVerb] = useState({});
    useEffect(() => {
      }, [passed_verb]);

    return(
        <div>
            <FormInput placeholder="Normal input" className="mb-2" onChange={(e) => setInputVerb(e.target.value)} placeholder='Ein Verb Hier' />
            <Input loading />
            <Button theme="primary" onClick={() => setVerb(verb) }>Konjugierst!</Button> 
        </div>
    )
}


export default InputVerb
